import React, { useEffect, useState } from "react";
import * as d3 from "d3";
import * as topojson from "topojson";
import Japandata from "./japanpos.json";

const ChoroplethMap = ({ features, now }) => {
  const width = 1800;
  const height = 800;
  const projection = d3.geoMercator().scale(1500).center([139.69167, 40.68944]);
  const path = d3.geoPath().projection(projection);
  const color = d3
    .scaleLinear()
    .domain(d3.extent(features, (feature) => feature.properties.value))
    .range(["#ccc", "#f00"]);

  const datas = Japandata;
  let x_pos,y_pos,lng_temp,lat_temp;
  return (
    <div>
      <svg width={width} height={height}>
        <g>
          {features.map((feature, i) => {
            return(
              <path key={i} d={path(feature)} fill="blue" stroke="white" />
            );
          })}

          {datas.data.map((item) => {
            lng_temp = item.lng;
            lat_temp = item.lat;
            x_pos = projection([lng_temp,lat_temp])[0];
            y_pos = projection([lng_temp,lat_temp])[1];
            console.log(projection([item.lng,item.lat]));
            return(
              <text 
                x={x_pos} 
                y={y_pos}
                fill="red"
              >テスト</text>
            )
          })}
        </g>
      </svg>
    </div>
  );
};
export default function ChoroplethMapPage() {
  const [features, setFeatures] = useState(null);
  useEffect(() => {
    (async() => {
      const res = await fetch(`${process.env.PUBLIC_URL}./data/japan.json`)
      const data = await res.json()
      const { features } = topojson.feature(data, data.objects.japan);
      setFeatures(features);
    })()
  },[])
  if (features == null) {
    return <p>loading</p>;
  }
  return <ChoroplethMap features={features} />;
};
